import React, { useContext, useState } from 'react';
import Carousel from 'react-gallery-carousel';
import 'react-gallery-carousel/dist/index.css';
import defaultImage from '../assets/defaultImages.jpeg';
import { BlogContext } from '../contextBlog';


const BlogDetail = () => {
    // const location = useLocation()
    // console.log(location, 'isi location');
    const { value } = useContext(BlogContext)
    const { singleBlog } = value
    const [blog, setBlog] = useState([])
    console.log(value, 'ini isi all Blogs', value.singleBlog);


    React.useEffect(() => {
        singleBlog.allBlogs._id.map(_id => {
            return setBlog((prev) => {
                return (
                    [...prev, _id]
                )
            })
        })
    }, [singleBlog])

    const images = singleBlog.blogData.photos.map((number) => ({
        src: `${number}`
    }));

    return (
        <div className="bdContainer">
            <div className="bdWrapper">
                Aloha Blog Detail
                <div className="carousel">
                    {images ?
                        <Carousel
                            images={images}
                            style={{ height: 250, width: 1050 }}
                            hasMediaButton={false}
                            hasThumbnails={false}
                            objectFit='cover'
                        /> : defaultImage}
                </div>

                <img src='' alt='' />
                <div className="bdContents">
                    <ul>
                        <li>halo 1</li>
                        <li>halo 2</li>
                        <li>halo 3</li>

                    </ul>
                    <div className="bdDetail">
                        <h1>{singleBlog.blogData.title}</h1>
                        <article>
                            {singleBlog.blogData.content}
                        </article>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default BlogDetail
