import React, { useEffect, useState } from 'react'
import axios from 'axios'
import BlogCard from './BlogCard'

const Blog = () => {

  const [blog, setBlog] = useState([])

  useEffect(() => {
    const getBlog = async () => {
      try {
        const dataBlog = await axios.get('http://localhost:5500/5R2I/blog')
        console.log(dataBlog.data);
        setBlog(dataBlog.data)
      } catch (error) {
        console.log(error);
      }
    }
    getBlog()
  }, [])

  return (
    <div className="deBlog">
      <div className="blogContainer">
        <h1 className="blogTitle grid-1">Latest Stories</h1>
        <span className='subHeader grid-2'>simply thought or just curiosity</span>
        {
          blog.map(item => {
            let gdate = item.createdAt;
            return (
              <BlogCard
                key={item._id}
                date={gdate || '12/02/2025'}
                topic={'graphic designs'}
                blogTitle={'all about packaging Designs'}
                glance={'lorem ipsum dolor siamet'}
                author={'John Doe'}
              />
            )
          })
        }
      </div>
    </div>
  )
}

export default Blog
