import React, { useEffect, useState } from 'react'
import axios from 'axios'
import BlogCard from './BlogCard'

const Blog = () => {

  const [blog, setBlog] = useState([])



  useEffect(() => {
    const getBlog = async () => {
      try {
        await axios.get('http://localhost:5500/5R2I/blog')
        console.log(getBlog)
        setBlog(getBlog.data)
      } catch (error) {
        console.log(getBlog.error);
      }
    }
    getBlog()
  }, [])

  return (
    <div className="deBlog">
      <div className="blogContainer">
        <h1 className="blogTitle grid-1">Latest Stories</h1>
        <span className='subHeader grid-2'>simply thought or just curiosity</span>
        <BlogCard
          date={'12/02/2025'}
          topic={'graphic designs'}
          blogTitle={'all about packaging Designs'}
          glance={'lorem ipsum dolor siamet'}
          author={'John Doe'}
        />
        <BlogCard
          date={'21/02/2025'}
          topic={'UI / UX'}
          blogTitle={'Seamless Pixels on Screen'}
          glance={'lorem ipsum dolor siamet'}
          author={'Jane Doe'}
        />
      </div>
    </div>
  )
}

export default Blog
