    SET_LOADING: 'SET_LOADING',
    GET_BLOG_SINGLE: 'GET_BLOG_SINGLE',
    GET_BLOGS: 'GET_BLOGS',
}

const BlogReducer = (state, action) => {
    switch (action.type) {
        case GetBlogActions.SET_LOADING:
            return { ...state, isLoading: true }
        case GetBlogActions.GET_BLOG_SINGLE:
            return {
                ...state,
                singleBlog: action.payload.single,
                isLoading: false
            }
        case GetBlogActions.GET_BLOGS:
            return {
                ...state,
                dataBlog: action.payload.blogs,
                isLoading: false
            }
        default:
            break;
    }
    return {
        isLoading: true,
        dataBlog: 'dataBlog',
