import React, { useReducer } from "react"
import axios from "axios"

export const BlogContext = React.createContext()

const BlogInitState = {
    isLoading: false,
    isERROR: false,
    blogId: '',
    allBlogs: [],
    singleBlog: {}
}

const GetBlogActions = {
    SET_LOADING: 'SET_LOADING',
    SET_ERROR: 'SET_ERROR',
    GET_BLOG_SINGLE: 'GET_BLOG_SINGLE',
    GET_BLOGS: 'GET_BLOGS',
}

const BlogReducer = (state, action) => {
    switch (action.type) {
        case GetBlogActions.SET_LOADING:
            return { ...state, isLoading: true }
        case GetBlogActions.GET_BLOG_SINGLE:
            return {
                ...state,
                singleBlog: action.payload.single,
                isLoading: false
            }
        case GetBlogActions.GET_BLOGS:
            return {
                ...state,
                allBlogs: action.payload.blogs,
                isLoading: false
            }
        case GetBlogActions.SET_ERROR:
            return {
                ...state,
                isERROR: action.payload.error,
            }
        default:
            break;
    }
    return {
        isLoading: true,
        allBlogs: 'dataBlog',
        singleBlog: 'data Single Blog'
    }
}


const BlogProvider = ({ children }) => {

    const [state, dispatch] = useReducer(BlogReducer, BlogInitState)

    const getSingleBlog = async (id) => {
        try {
            const blog = await axios.get(`http://localhost:5500/5R2I/blog/${id}`)
            dispatch({
                type: BlogReducer.GET_BLOG_SINGLE,
                payload: { blog }
            })
        } catch (error) {
            dispatch({
                type: BlogReducer.SET_ERROR,
                payload: { error: error.message }
            })
        }

    }

    return (
        <BlogContext.Provider
            value={{
                ...state,
                singleBlog: getSingleBlog
            }}
        >
            {children}
        </BlogContext.Provider>
    )
}
export default BlogProvider
