const BlogDetail = () => {
    const { value } = useContext(BlogContext)
    // const bude = value.singleBlog.blogData.photos
    console.log('ini isi all Blogs', value.singleBlog);
    const { singleBlog } = value

    const images = singleBlog.blogData.photos.map((number) => ({
        src: `${number}`
    }));

    return (
        <div className="bdContainer">
            <div className="bdWrapper">
                <Carousel
                    className='carousel'
                    images={images}
                    style={{ height: 500, width: 800 }}
                    hasMediaButton={false}
                    hasThumbnails={false}
                />

                <img src='' alt='' />
                <div className="bdContents">
                    <ul>
                        <li></li>
                    </ul>
                    <div className="bdDetail">
                        <h1>{singleBlog.blogData.title}</h1>
                        <article>
                            {singleBlog.blogData.content}
                        </article>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default BlogDetail
