import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function FadeMenu() {
    const [selectmenu, setSelectmenu] = useState(localStorage.getItem('pIndex'));

    useEffect(() => {
        console.log('idol ini', localStorage.getItem('pIndex'), selectmenu);
        // localStorage.removeItem('pIndex')
    })

    return (
        <div className='navContainer'>
            <ul className='menu' >
                <li><Link to="/" onClick={() => localStorage.setItem('pIndex', 0)} id={selectmenu === 0 && 'abdi'}>Home</Link></li>
                <li><Link to="portfolio" onClick={() => localStorage.setItem('pIndex', 1)} id={selectmenu === 1 && 'abdi'}>Portfolio</Link></li>
                <li><Link to='blog' onClick={() => localStorage.setItem('pIndex', 2)} id={selectmenu === 2 && 'abdi'}>Blog</Link></li>
            </ul>
        </div>
    );
}
